<?php
  $projectCounter = 1;
  foreach ($projects as $project) {
    if($projectCounter <= 4) {
      echo '<div class="dashboard-content-project">';
        echo '<p class="dashboard-content-project-deadline">Deadline: ' . $project->deadline  . '</p>';
        echo '<p class="dashboard-content-project-subtasks">4 Tasks</p>';
        echo '<h2>' . $project->name . '</h2>';
        echo '<p class="dashboard-content-project-description">' . $project->description . '</p>';
        echo '<div class="dashboard-content-project-controls-container">';
          echo '<img class="dashboard-content-project-controls-edit" src="data:image/svg+xml;utf8;base64," />';
          echo '<img class="dashboard-content-project-controls-trash" src="data:image/svg+xml;utf8;base64," />';
          echo '<p class="dashboard-content-project-controls-members">' . $project->members . ' Members</p>';
        echo '</div>';
      echo '</div>';
      $projectCounter++;
    }else {
      break;
    }
  }

?>
